<?php	require_once 'koneksi.php';	$db = new Koneksi();	$con=$db->connectMysql();	    function kdauto($tabel, $inisial){		global $con;		    	$struktur	= mysqli_query($con, "SELECT * FROM $tabel");    	$panjang	= '11';				while ($property = mysqli_fetch_field($struktur)) {			$field = $property->name;		}         	$qry	= mysqli_query($con, "SELECT max(".$field.") FROM ".$tabel);     	$row	= mysqli_fetch_array($qry);      	if ($row[0]=="") {     		$angka=0;    	}     	else {     		$angka		= substr($row[0], strlen($inisial));     	}    	     	$angka++;     	$angka	=strval($angka);      	$tmp	="";     	for($i=1; $i<=($panjang-strlen($inisial)-strlen($angka)); $i++) {    		$tmp=$tmp."0";	    	}     	return $inisial.$tmp.$angka;    }        // Konvesi dd-mm-yyyy -> yyyy-mm-dd    function tgl_ind_to_eng($tgl) {    	$tgl_eng=substr($tgl,6,4)."-".substr($tgl,3,2)."-".substr($tgl,0,2);     	return $tgl_eng;    }        // Konvesi yyyy-mm-dd -> dd-mm-yyyy    function tgl_eng_to_ind($tgl) {    	$tgl_ind=substr($tgl,8,2)."-".substr($tgl,5,2)."-".substr($tgl,0,4);    	return $tgl_ind;    }        function tgl_eng_to_ind2($tgl) {    	$tgl_ind=substr($tgl,8,2)."-".substr($tgl,5,2)."-".substr($tgl,0,4)." ".substr($tgl,-8);    	return $tgl_ind;    }        function format_angka($angka) {    	$hasil = number_format($angka,0, ",",".");    	return $hasil;    }        function terbilang($x)    {      $abil = array("", "Satu", "Dua", "Tiga", "Empat", "Lima", "Enam", "Tujuh", "Delapan", "Sembilan", "Sepuluh", "Sebelas");      if ($x < 12)        return " " . $abil[$x];      elseif ($x < 20)        return Terbilang($x - 10) . " Belas" ;      elseif ($x < 100)        return Terbilang($x / 10) . " Puluh" . Terbilang($x % 10);      elseif ($x < 200)        return " seratus" . Terbilang($x - 100);      elseif ($x < 1000)        return Terbilang($x / 100) . " Ratus" . Terbilang($x % 100);      elseif ($x < 2000)        return " seribu" . Terbilang($x - 1000);      elseif ($x < 1000000)        return Terbilang($x / 1000) . " Ribu" . Terbilang($x % 1000);      elseif ($x < 1000000000)        return Terbilang($x / 1000000) . " Juta" . Terbilang($x % 1000000);    }		function gen_uuid() {		return sprintf( '%04x%04x-%04x-%04x-%04x-%04x%04x%04x',			// 32 bits for "time_low"			mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff ),			// 16 bits for "time_mid"			mt_rand( 0, 0xffff ),			// 16 bits for "time_hi_and_version",			// four most significant bits holds version number 4			mt_rand( 0, 0x0fff ) | 0x4000,			// 16 bits, 8 bits for "clk_seq_hi_res",			// 8 bits for "clk_seq_low",			// two most significant bits holds zero and one for variant DCE1.1			mt_rand( 0, 0x3fff ) | 0x8000,			// 48 bits for "node"			mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff )		);	}		function acakangkahuruf($panjang)    {    	$karakter= 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz123456789';    	$string = '';    	for ($i = 0; $i < $panjang; $i++) {    		$pos = rand(0, strlen($karakter)-1);    		$string .= $karakter{$pos};    	}    	return $string;    }?>